<?php/*If you want to display a time in human readable form, pass the seconds to this functionand it will return an array, that you can use like this way:foreach ($units as $unit):	echo key($unit)." ";endforeach; */function secondsToHumanReadable($seconds) {	$units = array(		31556926 => array('anno', 'anni'),		2629744 => array('mese', 'mesi'),			604800 => array('settimana', 'settimane'),		86400 => array('giorno', 'giorni'),		3600 => array ('ora', 'ore'),		60 => array('minuto', 'minuti'),		1 => array ('secondo', 'secondi')	);	$result = array();	foreach($units as $divisor => $unitName) {		$units = intval($seconds / $divisor);		if ($units) {			$seconds %= $divisor;			$name = $units == 1 ? $unitName[0] : $unitName[1];			$result[] = "$units $name";		}	}	return implode(', ', $result);}/*	$units = array(	'settimane'	=> 604800, // 7*24*3600	'giorni'    =>  86400, //   24*3600	'ore'   	=>   3600,	'minuti' 	=>     60,	'secondi' 	=>      1	);		foreach ( $units as &$unit ) {		$quot  = intval($secs / $unit);		$secs -= $quot * $unit;		$unit  = $quot;	}	return $units;				// and in echo...						$time_string = '';					foreach ($units as $u => $v)						if (!empty($v))							$time_string.= $v.' '.$u.', ';					echo substr($time_string, 0, -2);	*/