<?phpfunction secondsToHumanReadableTerm($seconds) {	$units = array(		31556926 => array('anno', 'anni'),		2629744 => array('mese', 'mesi'),		604800 => array('settimana', 'settimane'),		86400 => array('giorno', 'giorni'),		3600 => array ('ora', 'ore'),		60 => array('minuto', 'minuti'),		1 => array ('secondo', 'secondi')	);	$result = array();	foreach($units as $divisor => $unitName) {		$units = intval($seconds / $divisor);		if ($units) {			$seconds %= $divisor;			$name = $units == 1 ? $unitName[0] : $unitName[1];			if ($units > 0)				return "$units $name";		}	}		return false;}?>